const empresa = body.empresa || '';
const msg = mensajes[empresa] || {
  titulo: empresa || 'Green Hub',
  slogan: 'Innovacion y sostenibilidad en cada paso',
  color: '#123840',
  intro: 'Hola <strong>' + body.nombre + '</strong>, gracias por tu interes en <strong>' + (empresa || 'nuestro ecosistema') + '</strong>, parte del grupo empresarial Green Hub.',
  destaque: 'Formamos parte de un ecosistema de 12 empresas enfocadas en sostenibilidad, innovacion y cafe de especialidad, con presencia en 5 paises.',
  cierre: 'Nuestro equipo te contactara proximamente para conversar sobre como podemos trabajar juntos.',
  prueba_social: '12 empresas | Sostenibilidad | Innovacion'
};

return [{ json: {
  newId,
  ownerNombre,
  timestamp,
  body,
  mensajeEmpresa: msg,
  row: [
    newId,
    timestamp,
    body.empresa || '',
    body.tipoReferido || '',
    body.nombre || '',
    body.empresaReferido || '',
    body.email || '',
    body.telefono || '',
    body.pais || '',
    body.linkedin || '',
    body.objetivo || '',
    body.valorPotencial || '',
    body.ownerEmail || '',
    ownerNombre,
    body.tuEmail || '',
    body.tuNombre || '',
    'Nuevo',
    timestamp,
    '',
    '',
    '',
    '',
    body.notas || '',
    '0'
  ],
  activityRow: [
    timestamp,
    newId,
    body.tuNombre + ' (' + body.tuEmail + ')',
    '',
    'Nuevo',
    'Referido registrado via landing page',
    ''
  ]
}}];
