const idsData = $('Read Referral IDs').first().json;

// Calcular siguiente ID
const idRows = (idsData.values || []).slice(1);
let maxId = 0;
for (const row of idRows) {
  const id = parseInt(row[0]);
  if (!isNaN(id) && id > maxId) maxId = id;
}
const newId = maxId + 1;

// Buscar nombre del owner
const userRows = (usersData.values || []).slice(1);
let ownerNombre = '';
for (const row of userRows) {
  if (row[1] === body.ownerEmail) {
    ownerNombre = row[0];
    break;
  }
}

