const body = $('Webhook').first().json.body;
const data = $input.first().json;
const rows = data.values || [];
if (rows.length < 2) return [{ json: { found: false, error: 'No hay referidos registrados' } }];

const targetId = parseInt(body.referralId);
if (isNaN(targetId)) return [{ json: { found: false, error: 'ID invalido' } }];

// Buscar fila por ID (columna A)
let rowIndex = -1;
let existingRow = null;
for (let i = 1; i < rows.length; i++) {
  if (parseInt(rows[i][0]) === targetId) {
    rowIndex = i + 1; // 1-indexed for Sheets API
    existingRow = rows[i];
    break;
  }
}

if (rowIndex === -1) return [{ json: { found: false, error: 'Referido #' + targetId + ' no encontrado' } }];

const timestamp = new Date().toISOString();
const oldEtapa = existingRow[16] || 'Nuevo';
const newEtapa = body.nuevaEtapa || oldEtapa;
const stageChanged = oldEtapa !== newEtapa;
const registradorEmail = existingRow[14] || '';
const registradorNombre = existingRow[15] || '';
const referidoNombre = existingRow[4] || '';
const referidoEmail = existingRow[6] || '';

// Preparar valores actualizados (columnas Q a X = indices 16-23)
const updatedRow = [...existingRow];
updatedRow[16] = newEtapa; // Etapa
updatedRow[17] = timestamp; // Ultima Actualizacion
updatedRow[18] = body.proximoPaso || existingRow[18] || ''; // Proximo Paso
updatedRow[19] = body.fechaFollowUp || existingRow[19] || ''; // Fecha Follow-up
if (newEtapa === 'Cerrado-Ganado') updatedRow[20] = 'Ganado';
else if (newEtapa === 'Cerrado-Perdido') updatedRow[20] = 'Perdido';
updatedRow[21] = body.razonCierre || existingRow[21] || ''; // Razon Cierre
if (body.notas) updatedRow[22] = (existingRow[22] || '') + '\n[' + timestamp.split('T')[0] + '] ' + body.notas;
updatedRow[23] = '0'; // Reset dias sin update

return [{ json: {
  found: true,
  targetId,
  rowIndex,
  stageChanged,
  oldEtapa,
  newEtapa,
  registradorEmail,
  registradorNombre,
  referidoNombre,
  referidoEmail,
  updatedRow,
  activityRow: [
    timestamp,
    targetId,
    'Via landing page',
    oldEtapa,
    newEtapa,
    body.notas || '',
    body.fechaFollowUp || ''
  ]
}}];
